mapper.select:


SELECT *
FROM HMW_USER
WHERE userId = #{userId} and pwd = #{pwd}


SELECT COUNT(userId)
FROM HMW_USER
WHERE userId = #{userId}


SELECT COUNT(nickName)
FROM HMW_USER
WHERE nickName = #{nickName}

SELECT userId, userName
FROM HMW_USER
WHERE nickName = #{nickName}

SELECT userId, userName, email
FROM HMW_USER
WHERE email = #{email}

SELECT userId, userName, email
FROM HMW_USER
WHERE email = #{email} AND userId = #{userId}

mapper.update:
UPDATE HMW_USER
SET pwd = #{pwd}
WHERE userId = #{userId}
